import java.lang.reflect.Field;

public class FieldTest {
    String outerField = "hello";

    class Inner {
        void check\(\) {
            System.out.println\("=== getDeclaredFields\(\) on " + getClass\(\).getSimpleName\(\) + " ==="\);
            for \(Field f : getClass\(\).getDeclaredFields\(\)\) {
                System.out.println\("  name=" + f.getName\(\) + " synthetic=" + f.isSynthetic\(\) + " type=" + f.getType\(\).getSimpleName\(\)\);
            }
            System.out.println\("=== getDeclaredField\('this$0'\) ==="\);
            try {
                Field f = getClass\(\).getDeclaredField\("this$0"\);
                System.out.println\("  FOUND: " + f.getName\(\) + " synthetic=" + f.isSynthetic\(\)\);
            } catch \(NoSuchFieldException e\) {
                System.out.println\("  NOT FOUND"\);
            }
        }
    }

    public static void main\(String[] args\) {
        FieldTest outer = new FieldTest\(\);
        FieldTest.Inner inner = outer.new Inner\(\);
        inner.check\(\);
    }
}
