import java.lang.reflect.Field;

public class FieldTest3 {
    String outerField = "hello";

    class InnerNoRef {
        // Does NOT reference outerField
    }

    class InnerWithRef {
        void doSomething\(\) {
            System.out.println\(outerField\); // References outer field
        }
    }

    public static void main\(String[] args\) {
        FieldTest3 outer = new FieldTest3\(\);
        FieldTest3.InnerNoRef noRef = outer.new InnerNoRef\(\);
        FieldTest3.InnerWithRef withRef = outer.new InnerWithRef\(\);

        System.out.println\("=== InnerNoRef getDeclaredFields\(\) ==="\);
        for \(Field f : noRef.getClass\(\).getDeclaredFields\(\)\) {
            System.out.println\("  " + f.getName\(\) + " synthetic=" + f.isSynthetic\(\)\);
        }

        System.out.println\("=== InnerWithRef getDeclaredFields\(\) ==="\);
        for \(Field f : withRef.getClass\(\).getDeclaredFields\(\)\) {
            System.out.println\("  " + f.getName\(\) + " synthetic=" + f.isSynthetic\(\)\);
        }
    }
}
